import React, { useEffect, useState } from 'react';
import mqtt from 'mqtt';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto'; // Import necessário para Chart.js

const App = () => {
  const [sensorData, setSensorData] = useState([]); // Estado para armazenar os dados dos sensores
  const [temperatureData, setTemperatureData] = useState([]);
  const [humidityData, setHumidityData] = useState([]);
  const [timestamps, setTimestamps] = useState([]);

  useEffect(() => {
    // Configuração do cliente MQTT
    const client = mqtt.connect('ws://192.168.0.100:9001'); // A URL do broker

    client.on('connect', () => {
      console.log('Conectado ao MQTT Broker');
      client.subscribe('esp32/sensordata', (err) => {
        if (err) console.error('Erro ao se inscrever no tópico:', err);
      });
    });

    client.on('message', (topic, message) => {
      const data = JSON.parse(message.toString());
      setSensorData((prevData) => [...prevData, data]);
      setTemperatureData((prevTemp) => [...prevTemp, data.temperature].slice(-10)); // Limita os últimos 10 pontos
      setHumidityData((prevHum) => [...prevHum, data.humidity].slice(-10)); // Limita os últimos 10 pontos
      setTimestamps((prevTime) => [...prevTime, new Date(data.timestamp).toLocaleTimeString()].slice(-10)); // Últimos 10 horários
    });

    return () => {
      if (client && client.connected) {
        client.end();
      }
    };
  }, []);

  const tempChartData = {
    labels: timestamps,
    datasets: [
      {
        label: 'Temperatura (°C)',
        data: temperatureData,
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        fill: true,
      },
    ],
  };

  const humidityChartData = {
    labels: timestamps,
    datasets: [
      {
        label: 'Umidade (%)',
        data: humidityData,
        borderColor: 'rgba(54, 162, 235, 1)',
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        fill: true,
      },
    ],
  };

  return (
    <div style={styles.app}>
      <h1 style={styles.title}>Monitoramento de Sensores</h1>
      <div style={styles.chartsContainer}>
        <div style={styles.chart}>
          <h3 style={styles.chartTitle}>Temperatura</h3>
          <Line data={tempChartData} />
        </div>
        <div style={styles.chart}>
          <h3 style={styles.chartTitle}>Umidade</h3>
          <Line data={humidityChartData} />
        </div>
      </div>
      <div style={styles.tableContainer}>
        <h3 style={styles.tableTitle}>Dados Recentes</h3>
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Temperatura (°C)</th>
              <th>Umidade (%)</th>
              <th>Horário</th>
            </tr>
          </thead>
          <tbody>
            {sensorData.slice(-10).map((data, index) => (
              <tr key={index}>
                <td>{data.temperature} °C</td>
                <td>{data.humidity} %</td>
                <td>{new Date(data.timestamp).toLocaleTimeString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default App;
