import React, { useState, useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';
import mqtt from 'mqtt';
import './App.css';

const App = () => {
  useEffect(() => {
    // Conectar ao broker MQTT usando TCP
    const client = mqtt.connect('192.168.0.100:1883'); // Ou use outro broker

    // Evento de conexão bem-sucedida
    client.on('connect', () => {
      console.log('Conectado ao MQTT Broker');
      client.subscribe('esp32/sensordata', (err) => {
        if (err) {
          console.log('Erro ao se inscrever no tópico:', err);
        } else {
          console.log('Inscrito no tópico "esp32/sensordata"');
        }
      });
    });

    // Evento de recebimento de mensagens
    client.on('message', (topic, message) => {
      console.log('Mensagem recebida:', topic, message.toString());
    });

    // Evento de desconexão
    client.on('close', () => {
      console.log('Desconectado do broker MQTT');
    });

    // Retornar uma função de limpeza quando o componente for desmontado
    return () => {
      client.end();
    };
  }, []);

  return (
    <div>
      <h1>Conexão MQTT</h1>
    </div>
  );
};

export default App;
