import React, { useState, useEffect } from 'react';
import mqtt from 'mqtt';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import './App.css';

const App = () => {
  const [sensorData, setSensorData] = useState([]);
  const [temperatureData, setTemperatureData] = useState([]);
  const [humidityData, setHumidityData] = useState([]);
  const [timestamps, setTimestamps] = useState([]);

  useEffect(() => {
    // Conectar ao broker MQTT
    const client = mqtt.connect('ws://192.168.0.100:9001');

    client.on('connect', () => {
      console.log('Conectado ao MQTT Broker');
      client.subscribe('esp32/sensordata', (err) => {
        if (err) console.error('Erro ao se inscrever no tópico:', err);
      });
    });

    client.on('message', (topic, message) => {
      const data = JSON.parse(message.toString());
      console.log('Dados recebidos no Frontend:', data);

      if (data.timestamp) {
        // Adicionar a hora local do navegador diretamente
        const localTime = new Date().toLocaleString('pt-BR', {
          timeZone: 'America/Sao_Paulo', // Fuso horário de São Paulo
          hour: '2-digit',
          minute: '2-digit',
          second: '2-digit',
        });

        setSensorData((prevData) => [...prevData, data]);
        setTemperatureData((prevTemp) => [...prevTemp, data.temperature].slice(-10));
        setHumidityData((prevHum) => [...prevHum, data.humidity].slice(-10));
        setTimestamps((prevTime) => [...prevTime, localTime].slice(-10));
      } else {
        console.error('Timestamp inválido recebido:', data.timestamp);
      }
    });

    return () => {
      if (client && client.connected) {
        client.end();
      }
    };
  }, []);

  // Função para enviar os dados com hora local para o Arduino
  const sendDataToArduino = (temperature, humidity) => {
    const localTime = new Date().toLocaleString('pt-BR', {
      timeZone: 'America/Sao_Paulo',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
    });

    const payload = {
      temperature,
      humidity,
      timestamp: localTime,
    };

    // Publicar no tópico do MQTT
    const client = mqtt.connect('ws://192.168.0.100:9001');
    client.on('connect', () => {
      console.log('Enviando dados com hora local para o Arduino');
      client.publish('esp32/sensordata', JSON.stringify(payload));
      client.end();
    });
  };

  const tempChartData = {
    labels: timestamps,
    datasets: [
      {
        label: 'Temperatura (°C)',
        data: temperatureData,
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        fill: true,
      },
    ],
  };

  const humidityChartData = {
    labels: timestamps,
    datasets: [
      {
        label: 'Umidade (%)',
        data: humidityData,
        borderColor: 'rgba(54, 162, 235, 1)',
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        fill: true,
      },
    ],
  };

  return (
    <div className="app">
      <h1 className="title">Fábrica XYZ - Monitoramento de Sensores</h1>
      <h3 className="title">Sensor DHT22: Temperatura e Umidade</h3>
      <div className="charts-container">
        <div className="chart">
          <h3 className="chart-title">Temperatura</h3>
          <Line data={tempChartData} />
        </div>
        <div className="chart">
          <h3 className="chart-title">Umidade</h3>
          <Line data={humidityChartData} />
        </div>
      </div>
      <div className="table-container">
        <h3 className="table-title">Dados Recentes</h3>
        <table>
          <thead>
            <tr>
              <th>Temperatura (°C)</th>
              <th>Umidade (%)</th>
              <th>Horário</th>
            </tr>
          </thead>
          <tbody>
            {sensorData.slice(-10).map((data, index) => (
              <tr key={index}>
                <td>{data.temperature} °C</td>
                <td>{data.humidity} %</td>
                <td>{data.timestamp}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default App;
