import React, { useState, useEffect } from 'react';
import mqtt from 'mqtt';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import './App.css';

const App = () => {
  const [sensorData, setSensorData] = useState([]);
  const [temperatureData, setTemperatureData] = useState([]);
  const [humidityData, setHumidityData] = useState([]);
  const [timestamps, setTimestamps] = useState([]);

  useEffect(() => {
    // Conectar ao broker MQTT
    const client = mqtt.connect('ws://192.168.0.100:9001');

    client.on('connect', () => {
      console.log('Conectado ao MQTT Broker');
      client.subscribe('esp32/sensordata', (err) => {
        if (err) console.error('Erro ao se inscrever no tópico:', err);
      });
    });

    client.on('message', (topic, message) => {
      const data = JSON.parse(message.toString());
      console.log('Dados recebidos no Frontend:', data);

      if (data.timestamp) {
        const parsedTimestamp = new Date(data.timestamp * 1000).toISOString(); // ISO 8601
        setSensorData((prevData) => [...prevData, data]);
        setTemperatureData((prevTemp) => [...prevTemp, data.temperature].slice(-10));
        setHumidityData((prevHum) => [...prevHum, data.humidity].slice(-10));
        setTimestamps((prevTime) => [...prevTime, parsedTimestamp].slice(-10));
      } else {
        console.error('Timestamp inválido recebido:', data.timestamp);
      }
    });

    return () => {
      if (client && client.connected) {
        client.end();
      }
    };
  }, []);

  const tempChartData = {
    labels: timestamps,
    datasets: [
      {
        label: 'Temperatura (°C)',
        data: temperatureData,
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        fill: true,
      },
    ],
  };

  const humidityChartData = {
    labels: timestamps,
    datasets: [
      {
        label: 'Umidade (%)',
        data: humidityData,
        borderColor: 'rgba(54, 162, 235, 1)',
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        fill: true,
      },
    ],
  };

  return (
    <div className="app">
      {/* Menu de Navegação */}
      <header className="header">
        <nav className="navbar">
          <a href="#home" className="nav-link">Home</a>
          <a href="#about" className="nav-link">Sobre Nós</a>
          <a href="#invest" className="nav-link">Dados</a>
          <a href="#contact" className="nav-link">Contato</a>
        </nav>
      </header>

      {/* Página Principal */}
      <section id="home" className="hero">
        <h1>Bem-vindo à Fábrica XYZ</h1>
        <p>
          Acompanhe o desempenho de nossos sensores e descubra como investir em nossa tecnologia de monitoramento.
        </p>
      </section>

      {/* Seção "Sobre Nós" */}
      <section id="about" className="about">
        <h2>Sobre Nós</h2>
        <p>
          Somos líderes no desenvolvimento de soluções IoT para fábricas inteligentes.
          Nossos sensores de última geração permitem monitoramento em tempo real, aumentando a eficiência e reduzindo custos.
        </p>
      </section>

      {/* Dados de Monitoramento */}
      <section id="invest" className="charts-container">
        <h2>Dados de Monitoramento</h2>
        <div className="chart">
          <h3>Temperatura</h3>
          <Line data={tempChartData} />
        </div>
        <div className="chart">
          <h3>Umidade</h3>
          <Line data={humidityChartData} />
        </div>
        <div className="table-container">
          <h3>Dados Recentes</h3>
          <table>
            <thead>
              <tr>
                <th>Temperatura (°C)</th>
                <th>Umidade (%)</th>
                <th>Horário (ISO 8601)</th>
              </tr>
            </thead>
            <tbody>
              {sensorData.slice(-10).map((data, index) => (
                <tr key={index}>
                  <td>{data.temperature} °C</td>
                  <td>{data.humidity} %</td>
                  <td>{data.timestamp}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      {/* Formulário de Contato */}
      <section id="contact" className="contact">
        <h2>Contato</h2>
        <form className="contact-form">
          <label>
            Nome:
            <input type="text" name="name" required />
          </label>
          <label>
            Email:
            <input type="email" name="email" required />
          </label>
          <label>
            Mensagem:
            <textarea name="message" required />
          </label>
          <button type="submit">Enviar</button>
        </form>
      </section>
    </div>
  );
};

export default App;
