import React, { useState, useEffect } from 'react';
import mqtt from 'mqtt';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import './App.css'; // Importa os estilos do arquivo App.css

const App = () => {
  const [sensorData, setSensorData] = useState([]);
  const [temperatureData, setTemperatureData] = useState([]);
  const [humidityData, setHumidityData] = useState([]);
  const [timestamps, setTimestamps] = useState([]);

  useEffect(() => {
    // Conectar ao broker MQTT
    const client = mqtt.connect('ws://192.168.0.100:9001');

    client.on('connect', () => {
      console.log('Conectado ao MQTT Broker');
      client.subscribe('esp32/sensordata', (err) => {
        if (err) console.error('Erro ao se inscrever no tópico:', err);
      });
    });

    client.on('message', (topic, message) => {
      const data = JSON.parse(message.toString());
      console.log('Dados recebidos no Frontend:', data);

      // Multiplica por 1000 para ajustar de segundos para milissegundos
      const parsedTimestamp = new Date(data.timestamp * 1000); // Verifique se `data.timestamp` está correto

      // Verifica se a data foi formatada corretamente
      console.log("Timestamp convertido:", parsedTimestamp);

      // Ajuste a formatação para garantir a exibição correta da data e hora
      const formattedTime = parsedTimestamp.toLocaleString('pt-BR', {
        timeZone: 'America/Sao_Paulo', // Ajuste de fuso horário
        hour12: false, // Isso garante que o horário seja no formato 24h
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
        day: '2-digit',
        month: '2-digit',
        year: 'numeric',
      });

      console.log("Horário formatado:", formattedTime);

      // Adiciona os dados ao estado
      setSensorData((prevData) => [
        ...prevData,
        { ...data, timestamp: formattedTime },
      ]);
      setTemperatureData((prevTemp) => [...prevTemp, data.temperature].slice(-10));
      setHumidityData((prevHum) => [...prevHum, data.humidity].slice(-10));
      setTimestamps((prevTime) => [...prevTime, formattedTime].slice(-10));
    });

    return () => {
      if (client && client.connected) {
        client.end();
      }
    };
  }, []);


  const tempChartData = {
    labels: timestamps,
    datasets: [
      {
        label: 'Temperatura (°C)',
        data: temperatureData,
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        fill: true,
      },
    ],
  };

  const humidityChartData = {
    labels: timestamps,
    datasets: [
      {
        label: 'Umidade (%)',
        data: humidityData,
        borderColor: 'rgba(54, 162, 235, 1)',
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        fill: true,
      },
    ],
  };

  return (
    <div className="app">
      <header className="header">
        <nav className="navbar">
          <ul>
            <li><a href="/">Home</a></li>
            <li><a href="/about">Sobre a Empresa</a></li>
            <li><a href="/contact">Contato</a></li>
            <li><a href="/investors">Investidores</a></li>
          </ul>
        </nav>
      </header>

      <h1 className="title">Fábrica XYZ - Monitoramento de Sensores</h1>
      <h3 className="title">Sensor DHT22: Temperatura e Umidade</h3>
      <div className="charts-container">
        <div className="chart">
          <h3 className="chart-title">Temperatura</h3>
          <Line data={tempChartData} />
        </div>
        <div className="chart">
          <h3 className="chart-title">Umidade</h3>
          <Line data={humidityChartData} />
        </div>
      </div>
      <div className="table-container">
        <h3 className="table-title">Dados Recentes</h3>
        <table>
          <thead>
            <tr>
              <th>Temperatura (°C)</th>
              <th>Umidade (%)</th>
              <th>Horário</th>
            </tr>
          </thead>
          <tbody>
            {sensorData.slice(-10).map((data, index) => (
              <tr key={index}>
                <td>{data.temperature} °C</td>
                <td>{data.humidity} %</td>
                <td>{data.timestamp}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <footer className="footer">
        <p>© 2024 Fábrica XYZ. Todos os direitos reservados.</p>
        <ul>
          <li><a href="/privacy">Política de Privacidade</a></li>
          <li><a href="/terms">Termos de Uso</a></li>
        </ul>
      </footer>
    </div>
  );
};

export default App;
