import React, { useEffect, useState } from 'react';
import mqtt from 'mqtt';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import './App.css'; 
import moment from 'moment';

const App = () => {
  const [sensorData, setSensorData] = useState([]);
  const [temperatureData, setTemperatureData] = useState([]);
  const [humidityData, setHumidityData] = useState([]);
  const [timestamps, setTimestamps] = useState([]);
  const timestamp = data.timestamp ? new Date(data.timestamp).toLocaleString() : new Date().toLocaleString();


  useEffect(() => {
    const client = mqtt.connect('ws://192.168.0.100:9001');

    client.on('connect', () => {
      console.log('Conectado ao MQTT Broker');
      client.subscribe('esp32/sensordata', (err) => {
        if (err) console.error('Erro ao se inscrever no tópico:', err);
      });
    });

    client.on('message', (topic, message) => {
      const data = JSON.parse(message.toString());
      const formattedTime = moment(data.timestamp).format('YYYY-MM-DD HH:mm:ss'); // Formatação do timestamp

      setSensorData((prevData) => [...prevData, { ...data, formattedTime }]);
      setTemperatureData((prevTemp) => [...prevTemp, data.temperature].slice(-10));
      setHumidityData((prevHum) => [...prevHum, data.humidity].slice(-10));
      setTimestamps((prevTime) => [...prevTime, formattedTime].slice(-10)); // Usando formattedTime aqui
    });

    return () => {
      if (client && client.connected) {
        client.end();
      }
    };
  }, []);

  const tempChartData = {
    labels: timestamps,
    datasets: [
      {
        label: 'Temperatura (°C)',
        data: temperatureData,
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        fill: true,
      },
    ],
  };

  const humidityChartData = {
    labels: timestamps,
    datasets: [
      {
        label: 'Umidade (%)',
        data: humidityData,
        borderColor: 'rgba(54, 162, 235, 1)',
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        fill: true,
      },
    ],
  };

  return (
    <div className="app">
      <h1 className="title">Fábrica XYZ - Monitoramento de Sensores</h1>
      <h3 className="title">Sensor DHT22: Temperatura e Umidade</h3>
      <div className="charts-container">
        <div className="chart">
          <h3 className="chart-title">Temperatura</h3>
          <Line data={tempChartData} />
        </div>
        <div className="chart">
          <h3 className="chart-title">Umidade</h3>
          <Line data={humidityChartData} />
        </div>
      </div>
      <div className="table-container">
        <h3 className="table-title">Dados Recentes</h3>
        <table>
          <thead>
            <tr>
              <th>Temperatura (°C)</th>
              <th>Umidade (%)</th>
              <th>Horário</th>
            </tr>
          </thead>
          <tbody>
            {sensorData.slice(-10).map((data, index) => (
              <tr key={index}>
                <td>{data.temperature} °C</td>
                <td>{data.humidity} %</td>
                <td>{data.formattedTime}</td> {/* Exibindo o horário formatado */}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default App;
