import React, { useEffect, useState } from 'react';
import mqtt from 'mqtt';

const App = () => {
  const [sensorData, setSensorData] = useState([]); // Estado para armazenar os dados dos sensores

  useEffect(() => {
    // Configuração do cliente MQTT
    const client = mqtt.connect('ws://192.168.0.100:9001');  // A URL do broker

    client.on('connect', () => {
      console.log('Conectado ao MQTT Broker');
      // Inscreve-se no tópico após a conexão ser estabelecida
      client.subscribe('esp32/sensordata', (err) => {
        if (err) {
          console.error('Erro ao se inscrever no tópico:', err);
        } else {
          console.log('Inscrito no tópico "esp32/sensordata"');
        }
      });
    });

    client.on('message', (topic, message) => {
      const data = JSON.parse(message.toString());
      console.log(data); 
      console.log('Dados recebidos:', data);
      setSensorData((prevData) => [...prevData, data]);  // Atualiza o estado com os novos dados
    });

    client.on('close', () => {
      console.log('Desconectado do broker MQTT');
    });

    client.on('error', (err) => {
      console.error('Erro no cliente MQTT:', err);
    });

    // Limpar a conexão quando o componente for desmontado
    return () => {
      if (client && client.connected) {
        client.end();
      }
    };
  }, []); // O array vazio significa que o efeito será executado apenas uma vez, quando o componente for montado

  return (
    <div>
      <h1>Aplicação IoT</h1>
      <p>Monitorando dados do sensor MQTT</p>
      <ul>
        {/* Exibindo os dados recebidos */}
        {sensorData.map((data, index) => (
          <li key={index}>
            <p>Temperatura: {data.temperature}°C</p>
            <p>Umidade: {data.humidity}%</p>
            <p>Timestamp: {new Date(data.timestamp).toLocaleString()}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
